private void Table_calKeyReleased(java.awt.event.KeyEvent evt) {                                      
        double basic;
        double hra;
        double arrear;
        double edu_all;
        double da;
        double ta;
        double total;
        double nps;
        int j=1;
        double sum,sum1=0.0,sum2=0.0,sum3=0.0,sum4=0.0,sum5=0.0,sum6=0.0,sum7;
        sum7 = 0.0;
        sum = 0.00;
        for(int i=0;i<=11;i++)
        {
                total = 0.0;
                String str = (String) Table_cal.getValueAt(i, j);
                basic = Float.parseFloat(str);
                sum = sum + basic;
                Table_cal.setValueAt(sum,12,j);
                da = (basic*15)/100;
                sum1 = sum1 + da;
                Table_cal.setValueAt(sum1,12,j+1);
                ta = (basic*10)/100;
                sum2 = sum2 + ta;
                Table_cal.setValueAt(sum2,12,j+2);
                Table_cal.setValueAt(da, i, j+1);
                Table_cal.setValueAt(ta, i, j+2);
                
                total = total + (basic+da+ta);
                Table_cal.setValueAt(total,i,j+6);
                str = (String) Table_cal.getValueAt(i,j+3);
                if(basic>=0 && basic<=25000)
                {
                    if ((basic * 10) / 100 >= Float.parseFloat(str))
                    {
                        hra = Float.parseFloat(str);
                        total = total + hra;
                        Table_cal.setValueAt(total, i, j+6);
                        sum3 = sum3 + hra;
                        Table_cal.setValueAt(sum3,12,j+3);
                    }
                    else
                    {
                        JOptionPane.showMessageDialog(rootPane, "Enter the value of HRA less than 10% of the Basic+GP as per Govt. of India Act.", "Invalid Input", JOptionPane.WARNING_MESSAGE);
                        Table_cal.setValueAt(null, i, j+3);
                    }
                }
                else if(basic>25000 && basic<=50000)
                {
                    if((basic * 20)/100 >= Float.parseFloat(str))
                    {
                        hra = Float.parseFloat(str);
                        total = total + hra;
                        Table_cal.setValueAt(total, i, j+6);
                        sum3 = sum3 + hra;
                        Table_cal.setValueAt(sum3,12,j+3);
                    }
                    else
                    {
                        JOptionPane.showMessageDialog(rootPane, "Enter the value of HRA less than 20% of the Basic+GP as per Govt. of India Act.", "Invalid Input", JOptionPane.WARNING_MESSAGE);
                        Table_cal.setValueAt(null, i, j+3);
                    }
                }
                else
                {
                    if((basic * 30)/100 >= Float.parseFloat(str))
                    {
                        hra = Float.parseFloat(str);
                        total = total + hra;
                        Table_cal.setValueAt(total, i, j+6);
                        sum3 = sum3 + hra;
                        Table_cal.setValueAt(sum3,12,j+3);
                    }
                    else
                    {
                        JOptionPane.showMessageDialog(rootPane, "Enter the value of HRA less than 30% of the Basic+GP as per Govt. of India Act.", "Invalid Input", JOptionPane.WARNING_MESSAGE);
                        Table_cal.setValueAt(null, i, j+3);
                    }

                }
                str = (String) Table_cal.getValueAt(i,j+4);
                arrear = Float.parseFloat(str);
                total = total + arrear;
                Table_cal.setValueAt(total,i,j+6);
                sum4 =sum4 + arrear;
                Table_cal.setValueAt(sum4,12,j+4);
                str = (String) Table_cal.getValueAt(i,j+5);
                edu_all = Float.parseFloat(str);
                total = total + edu_all;
                Table_cal.setValueAt(total, i, j+6);
                sum5 =sum5 + edu_all;
                Table_cal.setValueAt(sum5, 12, j+5);
                sum6 = sum6 + total;
                Table_cal.setValueAt(sum6,12,j+6);
                str = (String) Table_cal.getValueAt(i,j+7);
                if(total>=Float.parseFloat(str))
                {
                    nps = Float.parseFloat(str);
                    sum7 = sum7 + nps;
                    Table_cal.setValueAt(sum7,12,j+7);
                }
                else
                {
                    JOptionPane.showMessageDialog(rootPane, "Enter the value of NPS contribution less than Basic+GP", "Invalid Input", JOptionPane.WARNING_MESSAGE);
                    Table_cal.setValueAt(null, i, j+7);
                }
        }
        for(int l=1;l<8;l++)
        {
            for(int k=0;k<12;k++)
            {
                String str = (String) Table_cal.getValueAt(k,l);
                sum = sum + Float.parseFloat(str);
                Table_cal.setValueAt(sum,12,l);
            }
        }
}                                     

