    private void ResetActionPerformed(java.awt.event.ActionEvent evt) {                                      
        // TODO add your handling code here:
        if(evt.getSource()== Reset)
        {
            Text_field_name.setText(null);
            Text_field_emp_code.setText(null);
            Text_field_dept.setText(null);
            Text_Field_year1.setText(null);
            Text_field_year2.setText(null);
            for(int y=0;y<13;y++)
            {
                for(int z=1;z<9;z++)
                {
                    Table_cal.setValueAt(null, y, z);
                }
            }
            String arr1[] = {"March,","April,","May,","Jun,","July,","Aug,","Sept,","Oct,","Nov,","Dec,","Jan,","Feb,"};
            for(int col=0;col<12;col++)
            {
                Table_cal.setValueAt(arr1[col],col,0);
            }
            
        }
    }                                     

    private void Text_Field_year1KeyReleased(java.awt.event.KeyEvent evt) {                                             
        // TODO add your handling code here:
        int year1 = Integer.parseInt(Text_Field_year1.getText());
        int year2 = year1+1;
        String str = Integer.toString(year2);
        Text_field_year2.setText(str);
        String str2 = Integer.toString(year1);
        String arr[] = new String[12];
        
        for(int row=0;row<12;row++)
        {
            arr[row]=(String) Table_cal.getValueAt(row, 0);
        }
        
        for(int enter=0;enter<12;enter++)
        {
            if(Text_Field_year1.getText().length() == 4 && enter<10)
            {
                Table_cal.setValueAt(arr[enter]+str2, enter, 0);
            }
            if(Text_field_year2.getText().length() == 4 && enter>=10)
            {
                Table_cal.setValueAt(arr[enter]+str, enter, 0);
            }
        }
    }                                            

